import './styles.scss';

import $ from "jquery";
import popper from "popper.js";
import bootstrap from "bootstrap";

class chat_control {
    constructor() {
        this.msg_list = $('.msg-group');
    }

    send_msg(name, msg) {
        this.msg_list.append(this.get_msg_html(name, msg, 'right'));
        this.scroll_to_bottom(); 
    }

    receive_msg(name, msg) {
        this.msg_list.append(this.get_msg_html(name, msg, 'left'));
        this.scroll_to_bottom(); 
    }

    get_msg_html(name, msg, side) {
        var msg_temple = `
            <div class="card">
                 <div class="card-body">
                     <h6 class="card-subtitle mb-2 text-muted text-${side}">${name}</h6>
                     <p class="card-text float-${side}">${msg}</p>
                 </div>
            </div>
            `;
        return msg_temple;
    }

    scroll_to_bottom() {
        this.msg_list.scrollTop(this.msg_list[0].scrollHeight);
    }
}

var chat = new chat_control();
chat.receive_msg('yingshaoxo', 'This was made for you! \`2017/(520*1314)\`');

const send_button = $('button') // get jquery element from html table name
const input_box = $('#input-box') // get jquery element from div id
// also you could get it by $('.form-control') or $('textarea')

var ws_link = 'ws://' + location.hostname;
if (window.location.protocol === 'https:') {
    ws_link = 'wss://' + location.hostname;
}
var ws = new WebSocket(ws_link);

function handle_msg(msg) {
    msg = msg.trim()
    msg = msg.replace(/(?:\r\n|\r|\n)/g, '<br>')
    return msg
}

function get_msg_dict(text) {
    var dict = {};
    dict.name = Cookies.get('username');
    dict.text = text
    return dict
}

function send_msg() {
    let msg = handle_msg(input_box.val());
    if (msg != '') {
        let msg_dict = get_msg_dict(msg)
        ws.send(JSON.stringify(msg_dict));

        chat.send_msg('you', msg);
        input_box.val('');

        setTimeout(function(){ chat.receive_msg('yingshaoxo', 'I can hear you.'); }, 1000);
        MathJax.Hub.Queue(["Typeset",MathJax.Hub]);
    }
}

function autosize(){
  var el = this;
  setTimeout(function(){
    el.style.cssText = 'height:auto; padding:0';
    el.style.cssText = 'height:' + el.scrollHeight + 'px';

    send_button.get(0).style.cssText = 'height:auto; padding:0';
    send_button.get(0).style.cssText = 'height:' + el.scrollHeight + 'px';
  },0);
}

$.fn.selectRange = function(start, end) {
    if(end === undefined) {
        end = start;
    }
    return this.each(function() {
        if('selectionStart' in this) {
            this.selectionStart = start;
            this.selectionEnd = end;
        } else if(this.setSelectionRange) {
            this.setSelectionRange(start, end);
        } else if(this.createTextRange) {
            var range = this.createTextRange();
            range.collapse(true);
            range.moveEnd('character', end);
            range.moveStart('character', start);
            range.select();
        }
    });
};

function box_key_pressing(event) {
    // control + m was pressed
    if ((event.keyCode === 10 || event.keyCode === 77) && event.ctrlKey) {
        input_box.val(String.raw`
$$
\begin{align*}

\end{align*}
$$
        `.trim());
        input_box.focus();
        input_box.selectRange(18);
    }
    // control + enter was pressed
    if ((event.keyCode === 10 || event.keyCode === 13) && event.ctrlKey) {
        send_msg();
    }
    // esc was pressed
    if (event.keyCode === 27) {
        input_box.blur();
    }
}

send_button.on('click', send_msg.bind());
input_box.on('keyup', box_key_pressing.bind());
input_box.on('keydown', autosize);
